import { useState } from "react";
import React from 'react';


export const Productttt = () => {
    function useLocalState(localItem: string) {
        const [loc, setState] = useState(localStorage.getItem(localItem));

        function setLoc(newItem: string) {
            localStorage.setItem(localItem, newItem);
            setState(newItem)
        }

        return [loc, setLoc]
    }

    const [product, setProduct] = useLocalState('Product');

    return (
        <div>
            <p>
                Product :{product}
            </p>
            <button onClick={() => setProduct}>ahihi</button>
        </div>
    )
}
